public class InsertionSort{	public static int [] sort(int [] a){		for(int i = 0; i < a.length; i++){			int t = a[i];			int j = i;			while(( j > 0) && (a[j - 1]  > t)){				a[j] = a[j - 1];				j--;			}			a[j] = t;		}		return a;	}	public static void main(String[] args){		int [] a ={6,2,4,1,5,9};		System.out.println("≈≈–Ú«∞£∫");		printArray(a);		System.out.println("≈≈–Ú∫Û£∫");		sort(a);		printArray(a);	}		public static void printArray(int[] a){		for(int i : a){			System.out.print(i + ",");		}		System.out.println();	}}