public class QuickSort{	public static void sort(int [] a, int l, int r){		if(l < r){			int pivot = partition(a,l,r);			sort(a,l,pivot - 1);			sort(a,pivot + 1, r);		}	}		static int partition(int [] a, int i, int j){		int pivot = a[i];		while(i < j){			while(i < j && a[j] > pivot){				j--;			}			if(i < j){				a[i++] = a[j];			}			while(i < j && a[i] < pivot){				i++;			}			if(i < j){				a[j--] = a[i];			}		}		a[i] = pivot;		return i;	}			public static void main(String[] args){		int [] a ={6,2,4,1,5,9};		System.out.println("ÅÅÐòÇ°£º");		printArray(a);		System.out.println("ÅÅÐòºó£º");		sort(a,0,a.length-1);		printArray(a);	}		public static void printArray(int[] a){		for(int i : a){			System.out.print(i + ",");		}		System.out.println();	}}